import { Link } from 'react-router-dom';
import landingHero from '../../assets/landing-hero.jpg';
import socialFacebook from '../../assets/social-facebook.svg';
import socialTwitter from '../../assets/social-twitter.svg';
import socialInstagram from '../../assets/social-instagram.svg';
import socialLinkedIn from '../../assets/social-linkedin.svg';

export default function Landing() {
  return (
    <div className="bg-gradient-to-br from-blue-600 to-green-400 min-h-screen flex flex-col">
      {/* Hero Section */}
      <section className="flex flex-col md:flex-row items-center justify-between px-8 md:px-24 py-16 md:py-28 relative">
        <div className="flex-1 mb-12 md:mb-0">
          <h1 className="text-4xl md:text-5xl font-bold text-white mb-3">
            Fuel at Your{" "}
            <span className="text-green-300">Doorstep</span>
          </h1>
          <p className="text-white text-base md:text-lg mb-8 max-w-md">
            Premium fuel delivery service with verified partners. Fast, secure, and convenient fuel delivery right to your location.
          </p>
          <div className="space-x-4">
            <Link to="/login" className="bg-white text-blue-600 px-7 py-3 rounded-md font-semibold shadow hover:bg-blue-100 duration-200">
              Login
            </Link>
            <Link to="/register" className="bg-green-500 text-white px-7 py-3 rounded-md font-semibold shadow hover:bg-green-600 duration-200">
              Sign Up
            </Link>
          </div>
        </div>
        <div className="flex-1 flex justify-center">
          <img
            src={landingHero}
            alt="Fuel delivery truck in suburb"
            className="w-full max-w-md rounded-2xl shadow-2xl"
          />
        </div>
        <div className="absolute bottom-4 left-1/2 transform -translate-x-1/2">
          <span className="inline-block animate-bounce text-white text-3xl">â–¼</span>
        </div>
      </section>

      {/* ... keep the rest of your sections (Why Choose Us, Testimonials, Footer) ... */}
    </div>
  );
}
